import React from 'react';
import PropTypes from 'prop-types';
import { Table, Button } from 'react-bootstrap';

// FunciÃ³n para calcular la edad a partir de birth_date
const calculateAge = (birthDate) => {
  if (!birthDate) return '';
  const birth = new Date(birthDate);
  const diff = Date.now() - birth.getTime();
  const ageDate = new Date(diff);
  return Math.abs(ageDate.getUTCFullYear() - 1970);
};

const RoomsTable = ({ roomAssignments, onEditAssignment }) => {
    console.log('')
  return (
    <Table responsive bordered>
      <thead>
        <tr>
          <th>Nombre Completo</th>
          <th>Edad</th>
          <th>Sexo</th>
          <th>Pasaporte</th>
          <th>Fecha</th>
          <th>Ciudad</th>
          <th>Hotel</th>
          <th>Tipo de HabitaciÃ³n</th>
          <th>Precio</th>
          <th>Divisa</th>
          <th>NÂº HabitaciÃ³n</th>
          <th>Check-in</th>
          <th>Check-out</th>
          <th>Estado</th>
          <th>Notas</th>
          <th>Acciones</th>
        </tr>
      </thead>
      <tbody>
        {roomAssignments.length ? (
          roomAssignments.map((assignment) => {
            const fullName = `${assignment.first_name || ''} ${assignment.second_name || ''} ${assignment.paternal_surname || ''} ${assignment.mother_surname || ''}`.trim();
            const age = calculateAge(assignment.birth_date);
            const fecha = assignment.check_in_date ? assignment.check_in_date.split('T')[0] : ''; // O usar otro campo si se dispone
            return (
              <tr key={assignment.id}>
                <td>{fullName}</td>
                <td>{age}</td>
                <td>{assignment.sex || '-'}</td>
                <td>{assignment.passport || '-'}</td>
                <td>{fecha}</td>
                <td>{assignment.city || '-'}</td>
                <td>{assignment.hotel_name || '-'}</td>
                <td>{assignment.type || '-'}</td>
                <td>{assignment.price != null ? assignment.price : '-'}</td>
                <td>{assignment.currency || '-'}</td>
                <td>{assignment.room_number || '-'}</td>
                <td>{assignment.check_in_date ? assignment.check_in_date.split('T')[0] : '-'}</td>
                <td>{assignment.departure_date ? assignment.departure_date.split('T')[0] : '-'}</td>
                <td>{assignment.status || '-'}</td>
                <td>{assignment.comments || '-'}</td>
                <td>
                  <Button variant="primary" size="sm" onClick={() => onEditAssignment(assignment)}>
                    Editar
                  </Button>
                </td>
              </tr>
            );
          })
        ) : (
          <tr>
            <td colSpan="16" className="text-center">No hay asignaciones de cuartos.</td>
          </tr>
        )}
      </tbody>
    </Table>
  );
};

RoomsTable.propTypes = {
  roomAssignments: PropTypes.array.isRequired,
  onEditAssignment: PropTypes.func.isRequired,
};

export default RoomsTable;
