import React, { useState, useEffect } from 'react';
import { Table, Spinner } from 'react-bootstrap';

const HotelAssignmentsTable = ({ idGroup }) => {
  const [loading, setLoading] = useState(true);
  const [assignments, setAssignments] = useState([]);

  const fetchHotelReservations = () => {
    const apiUrl = `${process.env.REACT_APP_API_URL}/hotels_reservation/get_hotel_reservation`;
    const params = new URLSearchParams({ id_grupo: idGroup });
    fetch(`${apiUrl}?${params.toString()}`)
      .then((response) => {
        if (!response.ok) {
          throw new Error('Error al obtener asignaciones de hoteles');
        }
        return response.json();
      })
      .then((data) => {
        // Asumimos que el backend devuelve un objeto con la propiedad hotel_assignments
        setAssignments(data.hotel_assignments || []);
      })
      .catch((error) => console.error(error))
      .finally(() => setLoading(false));
  };

  useEffect(() => {
    fetchHotelReservations();
  }, [idGroup]);

  if (loading) {
    return (
      <div className="text-center py-3">
        <Spinner animation="border" role="status">
          <span className="visually-hidden">Cargando...</span>
        </Spinner>
      </div>
    );
  }

  if (!assignments.length) {
    return <p>No hay asignaciones de hoteles para este grupo.</p>;
  }

  return (
    <Table striped bordered hover responsive>
      <thead>
        <tr>
          <th>Ciudad</th>
          <th>Fecha</th>
          <th>Hotel</th>
          <th>Check-in</th>
          <th>Check-out</th>
          <th>Rooming List</th>
          <th>Pro Forma</th>
          <th>Moneda</th>
          <th>Total a Pagar</th>
          <th>Fecha de Pago</th>
          <th>Fecha de Pago Realizado</th>
          <th>Factura</th>
          <th>IGA</th>
          <th>Notas</th>
          <th>Acciones</th>
        </tr>
      </thead>
      <tbody>
        {assignments.map((assignment, idx) => (
          <tr key={idx}>
            <td>{assignment.city}</td>
            <td>{assignment.date}</td>
            <td>{assignment.hotel_name}</td>
            <td>{assignment.check_in}</td>
            <td>{assignment.check_out}</td>
            <td>{assignment.rooming_list ? 'Sí' : 'No'}</td>
            <td>{assignment.pro_forma ? 'Sí' : 'No'}</td>
            <td>{assignment.currency}</td>
            <td>{assignment.total_to_pay}</td>
            <td>{assignment.payment_date}</td>
            <td>{assignment.payment_done_date || '-'}</td>
            <td>{assignment.factura ? 'Sí' : 'No'}</td>
            <td>{assignment.iga ? 'Sí' : 'No'}</td>
            <td>{assignment.notes}</td>
            <td>
              {/* Aquí puedes agregar botones para editar o borrar la asignación */}
              <button className="btn btn-sm btn-primary">Editar</button>
              <button className="btn btn-sm btn-danger ms-1">Borrar</button>
            </td>
          </tr>
        ))}
      </tbody>
    </Table>
  );
};

export default HotelAssignmentsTable;
