import React, { useState, useEffect } from 'react';
import PropTypes from 'prop-types';
import { Modal, Button, Form, Row, Col, Alert } from 'react-bootstrap';

// Función para calcular la edad a partir de la fecha de nacimiento
const calculateAge = (birthDate) => {
  if (!birthDate) return '';
  const birth = new Date(birthDate);
  const diff = Date.now() - birth.getTime();
  const ageDate = new Date(diff);
  return Math.abs(ageDate.getUTCFullYear() - 1970);
};

const RoomAssignmentModal = ({ show, onHide, initialData, onSave, availableHotels }) => {
  // Campos editables
  const [hotelId, setHotelId] = useState(initialData.id_hotel || '');
  const [roomType, setRoomType] = useState(initialData.type || '');
  const [roomNumber, setRoomNumber] = useState(initialData.room_number || '');
  const [notes, setNotes] = useState(initialData.comments || '');
  const [validationMsg, setValidationMsg] = useState('');

  // Opciones para tipo de habitación y sus datos (precio, currency)
  const [roomTypeOptions, setRoomTypeOptions] = useState([]);
  const [selectedRoomInfo, setSelectedRoomInfo] = useState(null);

  // Campos para suplementos
  const [supplement, setSupplement] = useState('');
  const [supplementCurrency, setSupplementCurrency] = useState('EUR');

  // Estados para datos que se actualizan automáticamente al seleccionar hotel
  const [city, setCity] = useState(initialData.city || '');
  // Usamos "datetime-local" para incluir la hora
  const [checkIn, setCheckIn] = useState(
    initialData.start_date ? initialData.start_date.slice(0, 16) : ''
  );
  const [checkOut, setCheckOut] = useState(
    initialData.end_date ? initialData.end_date.slice(0, 16) : ''
  );

  // Al seleccionar un hotel, se actualizan las opciones de habitación y se actualizan ciudad, check-in y check-out.
  useEffect(() => {
    if (hotelId) {
      // Llamar al endpoint para obtener tipos de habitación disponibles para el hotel seleccionado
      fetch(`${process.env.REACT_APP_API_URL}/hotels_room/hotels_room?id_hotel=${hotelId}`)
        .then((res) => res.json())
        .then((data) => {
          if (data.status === 'success' && Array.isArray(data.data)) {
            setRoomTypeOptions(data.data);
          } else {
            setRoomTypeOptions([]);
          }
        })
        .catch((err) => {
          console.error('Error al obtener tipos de habitación:', err);
          setRoomTypeOptions([]);
        });
      
      // Actualizar ciudad y fechas usando availableHotels
      const selectedHotel = availableHotels.find(h => h.id_hotel.toString() === hotelId);
      if (selectedHotel) {
        setCity(selectedHotel.city || '');
        // Suponemos que el endpoint devuelve check_in_date y departure_date con hora (en formato ISO)
        setCheckIn(selectedHotel.check_in_date ? selectedHotel.check_in_date.slice(0, 16) : '');
        setCheckOut(selectedHotel.departure_date ? selectedHotel.departure_date.slice(0, 16) : '');
      }
    } else {
      setRoomTypeOptions([]);
      setSelectedRoomInfo(null);
    }
  }, [hotelId, availableHotels]);

  // Cuando se selecciona un tipo de habitación, se actualizan automáticamente precio y divisa.
  const handleRoomTypeChange = (e) => {
    const selectedType = e.target.value;
    setRoomType(selectedType);
    const roomInfo = roomTypeOptions.find(rt => rt.type === selectedType);
    setSelectedRoomInfo(roomInfo || null);
  };

  // Función para guardar la asignación
  const handleSubmit = (e) => {
    e.preventDefault();
    if (!hotelId) {
      setValidationMsg('Debe seleccionar un hotel.');
      return;
    }
    if (!roomType) {
      setValidationMsg('Debe seleccionar un tipo de habitación.');
      return;
    }
    if (!roomNumber) {
      setValidationMsg('Debe ingresar el número de habitación.');
      return;
    }
    // Construir payload según la estructura requerida
    const payload = {
      id_days: initialData.id_day, // Asumimos que initialData.id_day es el string del día
      id_hotel_reservation: initialData.id || 0, // Si es edición, usar el id de la asignación
      client_id: initialData.id_clients,
      id_room: initialData.id_room || 0,
      room_number: roomNumber,
      check_in_date: checkIn ? new Date(checkIn).toISOString() : null,
      departure_date: checkOut ? new Date(checkOut).toISOString() : null,
      price: selectedRoomInfo ? selectedRoomInfo.price : null,
      currency: selectedRoomInfo ? selectedRoomInfo.currency : null,
      complement: supplement ? Number(supplement) : 0,
      complement_currency: supplementCurrency,
      status: "New", // o el valor que corresponda
      comments: notes,
    };
    onSave(payload)
      .then(() => {
        onHide();
      })
      .catch((err) => {
        setValidationMsg(err.message || 'Error al guardar la asignación.');
      });
  };

  return (
    <Modal show={show} onHide={onHide} centered size="xl">
      <Form onSubmit={handleSubmit}>
        <Modal.Header closeButton>
          <Modal.Title>{initialData.id ? 'Editar Asignación de Cuarto' : 'Asignar Cuarto'}</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          {/* Fila 1: Información del Cliente */}
          <Row className="mb-2">
            <Col>
              <strong>Cliente:</strong> {`${initialData.first_name} ${initialData.second_name} ${initialData.paternal_surname} ${initialData.mother_surname}`}
            </Col>
          </Row>
          {/* Fila 2: Datos Personales */}
          <Row className="mb-2">
            <Col md={4}>
              <strong>Edad:</strong> {calculateAge(initialData.birth_date)}
            </Col>
            <Col md={4}>
              <strong>Sexo:</strong> {initialData.sex || '-'}
            </Col>
            <Col md={4}>
              <strong>Pasaporte:</strong> {initialData.passport || '-'}
            </Col>
          </Row>
          {/* Fila 3: Fecha y Ciudad Original */}
          <Row className="mb-3">
            <Col md={6}>
              <strong>Fecha:</strong> {initialData.date}
            </Col>
            <Col md={6}>
              <strong>Ciudad:</strong> {initialData.city}
            </Col>
          </Row>
          {/* Fila 4: Selección de Hotel */}
          <Form.Group controlId="editHotel" className="mb-3">
            <Form.Label>Hotel</Form.Label>
            <Form.Select
              value={hotelId}
              onChange={(e) => setHotelId(e.target.value)}
            >
              <option value="">Seleccione un hotel</option>
              {availableHotels.map((h) => (
                <option key={h.id_hotel} value={h.id_hotel}>{h.hotel_name}</option>
              ))}
            </Form.Select>
          </Form.Group>
          {/* Fila 5: Mostrar Ciudad y Fechas Actualizadas (solo lectura) */}
          <Row className="mb-3">
            <Col md={4}>
              <Form.Group controlId="displayCity">
                <Form.Label>Ciudad Actualizada</Form.Label>
                <Form.Control type="text" value={city} readOnly />
              </Form.Group>
            </Col>
            <Col md={4}>
              <Form.Group controlId="displayCheckIn">
                <Form.Label>Check-in (Fecha y Hora)</Form.Label>
                <Form.Control type="datetime-local" value={checkIn} readOnly />
              </Form.Group>
            </Col>
            <Col md={4}>
              <Form.Group controlId="displayCheckOut">
                <Form.Label>Check-out (Fecha y Hora)</Form.Label>
                <Form.Control type="datetime-local" value={checkOut} readOnly />
              </Form.Group>
            </Col>
          </Row>
          {/* Fila 6: Selección de Tipo de Habitación y visualización de Precio y Divisa */}
          <Row className="mb-3">
            <Col md={5}>
              <Form.Group controlId="editRoomType">
                <Form.Label>Tipo de Habitación</Form.Label>
                <Form.Select
                  value={roomType}
                  onChange={handleRoomTypeChange}
                >
                  <option value="">Seleccione un tipo</option>
                  {roomTypeOptions.map((rt) => (
                    <option key={rt.id_room} value={rt.type}>
                      {rt.type}
                    </option>
                  ))}
                </Form.Select>
              </Form.Group>
            </Col>
            <Col md={3}>
              <Form.Group controlId="displayPrice">
                <Form.Label>Precio</Form.Label>
                <Form.Control
                  type="number"
                  value={selectedRoomInfo ? selectedRoomInfo.price : ''}
                  readOnly
                />
              </Form.Group>
            </Col>
            <Col md={4}>
              <Form.Group controlId="displayCurrency">
                <Form.Label>Divisa</Form.Label>
                <Form.Control
                  type="text"
                  value={selectedRoomInfo ? selectedRoomInfo.currency : ''}
                  readOnly
                />
              </Form.Group>
            </Col>
          </Row>
          {/* Fila 7: Suplemento y Divisa de Suplemento */}
          <Row className="mb-3">
            <Col md={6}>
              <Form.Group controlId="editSupplement">
                <Form.Label>Suplemento</Form.Label>
                <Form.Control
                  type="number"
                  value={supplement}
                  onChange={(e) => setSupplement(e.target.value)}
                  placeholder="Ingrese suplemento (opcional)"
                />
              </Form.Group>
            </Col>
            <Col md={6}>
              <Form.Group controlId="editSupplementCurrency">
                <Form.Label>Divisa Suplemento</Form.Label>
                <Form.Select
                  value={supplementCurrency}
                  onChange={(e) => setSupplementCurrency(e.target.value)}
                >
                  <option value="EUR">EUR</option>
                  <option value="USD">USD</option>
                  <option value="CHF">CHF</option>
                  <option value="GBP">GBP</option>
                  {/* Más opciones según se requiera */}
                </Form.Select>
              </Form.Group>
            </Col>
          </Row>
          {/* Fila 8: Número de Habitación */}
          <Form.Group controlId="editRoomNumber" className="mb-3">
            <Form.Label>Número de Habitación</Form.Label>
            <Form.Control
              type="text"
              value={roomNumber}
              onChange={(e) => setRoomNumber(e.target.value)}
              placeholder="Ingrese el número de habitación"
            />
          </Form.Group>
          {/* Fila 9: Notas / Comentarios */}
          <Form.Group controlId="editNotes" className="mb-3">
            <Form.Label>Notas / Comentarios</Form.Label>
            <Form.Control
              as="textarea"
              rows={3}
              value={notes}
              onChange={(e) => setNotes(e.target.value)}
            />
          </Form.Group>
          {validationMsg && <Alert variant="danger">{validationMsg}</Alert>}
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={onHide}>Cancelar</Button>
          <Button variant="primary" type="submit">Guardar Cambios</Button>
        </Modal.Footer>
      </Form>
    </Modal>
  );
};

RoomAssignmentModal.propTypes = {
  show: PropTypes.bool.isRequired,
  onHide: PropTypes.func.isRequired,
  initialData: PropTypes.object.isRequired,
  onSave: PropTypes.func.isRequired,
  availableHotels: PropTypes.array.isRequired,
};

export default RoomAssignmentModal;
