import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Card, Badge, Tab, Nav, Spinner } from 'react-bootstrap';
import { useParams, useLocation, Link } from 'react-router-dom';

import ClientsTable from '../components/ClientsTable';
import OptionalsSection from '../components/OptionalsSection';

// Importa los modales (a crear en otros archivos)
import EditGuideModal from '../components/EditGuideModal';
import EditBusModal from '../components/EditBusModal';
import EditQRModal from '../components/EditQRModal';
import EditOperationsAgentModal from '../components/EditOperationsAgentModal';
import EditAssistantModal from '../components/EditAssistantModal';
import EditResponsibleHotelsModal from '../components/EditResponsibleHotelsModal';
import AddOptionalModal from '../components/AddOptionalModal';
import EditOptionalModal from '../components/EditOptionalModal';
import DeleteOptionalModal from '../components/DeleteOptionalModal';
// ... otros modales que necesites
// Si decides usar react-leaflet para el mapa, importar sus componentes:
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';


const GroupDetail = () => {
  const { id } = useParams();
  const location = useLocation();

  // Extraer parámetros de la query string
  const queryParams = new URLSearchParams(location.search);
  const table = queryParams.get('table') || 'clientes'; // Puedes definir un valor por defecto
  const filters = queryParams.get('filters') || null;

  // Estados para almacenar los datos del grupo
  const [groupData, setGroupData] = useState(null);
  const [tableData, setTableData] = useState([]);
  const [itinerary, setItinerary] = useState([]);
  const [clientAges, setClientAges] = useState({});
  const [loading, setLoading] = useState(true);

  // Estados para el control de modales
  const [showEditGuideModal, setShowEditGuideModal] = useState(false);
  const [showEditBusModal, setShowEditBusModal] = useState(false);
  const [showEditQRModal, setShowEditQRModal] = useState(false);
  const [showEditOperationsAgentModal, setShowEditOperationsAgentModal] = useState(false);
  const [showEditAssistantModal, setShowEditAssistantModal] = useState(false);
  const [showEditResponsibleHotelsModal, setShowEditResponsibleHotelsModal] = useState(false);
  const [showAddOptionalModal, setShowAddOptionalModal] = useState(false);
  const [showEditOptionalModal, setShowEditOptionalModal] = useState(false);
  const [showDeleteOptionalModal, setShowDeleteOptionalModal] = useState(false);
  const [currentOptionalData, setCurrentOptionalData] = useState(null);
  // ... otros modales

  // Obtener datos del grupo mediante API
  useEffect(() => {
    // Se construye la URL para obtener datos del grupo (ajusta la URL base según corresponda)
    const apiUrl = `${process.env.REACT_APP_API_URL}/groups/group_data`;
    const params = new URLSearchParams({  id_group: id, table });
    if (filters) {
      params.append('filters', filters);
    }
    fetch(`${apiUrl}?${params.toString()}`)
      .then(response => {
        if (!response.ok) throw new Error('Error al obtener los datos del grupo');
        return response.json();
      })
      .then(data => {
        setGroupData(data.group_data);
        setTableData(data.table_data);
        setItinerary(data.itinerary || []);
        setClientAges(data.client_ages || {});
      })
      .catch(error => console.error('Error al obtener el detalle del grupo:', error))
      .finally(() => setLoading(false));
  }, [id, table, filters]);

  if (loading) {
    return (
      <Container className="text-center py-5">
        <Spinner animation="border" role="status">
          <span className="visually-hidden">Cargando...</span>
        </Spinner>
      </Container>
    );
  }

  if (!groupData) {
    return <Container><p>Error: Grupo no encontrado.</p></Container>;
  }

  return (
    <Container fluid>
      {/* Información General */}
      <Row className="mt-3">
        <Col md={3}>
        <Card style={{ backgroundColor: '#BDD8F1', borderRadius: '10px' }}>
            <Card.Body className="p-2">
            <h5>Información General</h5>
            <Row>
                <Col>
                <p><strong>ID Grupo:</strong> {groupData.id_group}</p>
                <p>
                    <strong>Guía:</strong> <span id="guideName">{groupData.guide_name || 'No asignado'}</span>
                    <Link className="ms-2 edit-icon" onClick={() => setShowEditGuideModal(true)} to="#">
                    <i className="fas fa-edit"></i>
                    </Link>
                </p>
                <p>
                    <strong>Bus:</strong> <span id="busInfo">{groupData.bus_company || 'No asignado'} {groupData.bus_code ? ' - ' + groupData.bus_code : ''}</span>
                    <Link className="ms-2 edit-icon" onClick={() => setShowEditBusModal(true)} to="#">
                    <i className="fas fa-edit"></i>
                    </Link>
                </p>
                </Col>
                <Col>
                <p>
                    <strong>Status:</strong> 
                    <Badge pill bg="success" style={{ fontSize: '1rem', padding: '5px', marginLeft: '10px' }}>
                    {groupData.status.charAt(0).toUpperCase() + groupData.status.slice(1)}
                    </Badge>
                </p>
                <p>
                    <strong>QR:</strong> <span id="qrStatus">{groupData.QR ? 'Sí' : 'No'}</span>
                    <Link className="ms-2 edit-icon" onClick={() => setShowEditQRModal(true)} to="#">
                    <i className="fas fa-edit"></i>
                    </Link>
                </p>
                </Col>
            </Row>
            </Card.Body>
          </Card>
        </Col>

        {/* Itinerario */}
        <Col md={5}>
          <Card style={{ backgroundColor: '#BDD8F1', borderRadius: '10px' }}>
            <Card.Body className="p-2">
              <h5>Itinerario</h5>
              <Row>
                <Col>
                  <p><strong>Vuelo de Llegada:</strong> {groupData.start_flight || 'No asignado'}</p>
                  <p><strong>Fecha de Inicio:</strong> <span id="fechaInicio">{groupData.start_date}</span></p>
                  <p><strong>Ciudad Actual:</strong> {groupData.ciudad_actual || 'N/A'}</p>
                </Col>
                <Col>
                  <p><strong>Vuelo de Regreso:</strong> {groupData.end_flight || 'No asignado'}</p>
                  <p><strong>Fecha de Regreso:</strong> <span id="fechaRegreso">{groupData.end_date}</span></p>
                  <p><strong>Hotel Actual:</strong> {groupData.hotel_actual || 'N/A'}</p>
                </Col>
              </Row>
            </Card.Body>
          </Card>
        </Col>

        {/* Contacto y Mapa Mini */}
        <Col md={4}>
          <Card style={{ backgroundColor: '#BDD8F1', borderRadius: '10px' }}>
            <Card.Body className="p-2">
              <Row>
                <Col xs={6}>
                  <h5>Contacto</h5>
                  <p>
                    <strong>Operaciones:</strong> <span id="operationsAgentInfo">{groupData.operaciones_name || 'No asignado'}</span>
                    <Link className="ms-2 edit-icon" onClick={() => setShowEditOperationsAgentModal(true)} to="#">
                      <i className="fas fa-edit"></i>
                    </Link>
                  </p>
                  <p>
                    <strong>Asistente:</strong> <span id="assistantInfo">{groupData.nombre_asistente || 'No asignado'}</span>
                    <Link className="ms-2 edit-icon" onClick={() => setShowEditAssistantModal(true)} to="#">
                      <i className="fas fa-edit"></i>
                    </Link>
                  </p>
                  <p>
                    <strong>Responsable de Hoteles:</strong> <span id="responsibleHotelsInfo">{groupData.id_responsible_hotels || 'No asignado'}</span>
                    <Link className="ms-2 edit-icon" onClick={() => setShowEditResponsibleHotelsModal(true)} to="#">
                      <i className="fas fa-edit"></i>
                    </Link>
                  </p>
                </Col>
                <Col xs={6} className="d-flex align-items-start justify-content-center">
                  {/* Mapa Mini */}
                  <Link onClick={() => {/* Abrir modal de mapa */}} to="#">
                    <img src="/assets/images/mini_mapa.png" alt="Mapa" className="img-fluid mini-map-img" style={{ borderRadius: 0, maxWidth: '90%' }} />
                  </Link>
                </Col>
              </Row>
            </Card.Body>
          </Card>
        </Col>
      </Row>

      {/* Pestañas para Clientes, Hoteles, etc. */}
      <Row className="mt-3">
        <Col>
          <Tab.Container defaultActiveKey="clientes">
            <Nav variant="tabs">
              <Nav.Item>
                <Nav.Link eventKey="clientes" as={Link} to={`/grupo/${id}?table=clientes`}>Clientes</Nav.Link>
              </Nav.Item>
              <Nav.Item>
                <Nav.Link eventKey="hoteles" as={Link} to={`/grupo/${id}?table=hoteles`}>Hoteles</Nav.Link>
              </Nav.Item>
              <Nav.Item>
                <Nav.Link eventKey="cuartos" as={Link} to={`/grupo/${id}?table=cuartos`}>Cuartos</Nav.Link>
              </Nav.Item>
              <Nav.Item>
                <Nav.Link eventKey="opcionales" as={Link} to={`/grupo/${id}?table=opcionales`}>Opcionales</Nav.Link>
              </Nav.Item>
              <Nav.Item>
                <Nav.Link eventKey="liquidacion" as={Link} to={`/grupo/${id}?table=liquidacion`}>Liquidación</Nav.Link>
              </Nav.Item>
            </Nav>
            <Tab.Content className="mt-3">
              <Tab.Pane eventKey="clientes">
                {/* Renderizar la tabla de clientes */}
                <ClientsTable data={tableData} />
              </Tab.Pane>
              <Tab.Pane eventKey="hoteles">
                <p>Contenido de hoteles no implementado aún.</p>
              </Tab.Pane>
              <Tab.Pane eventKey="cuartos">
                <p>Contenido de cuartos no implementado aún.</p>
              </Tab.Pane>
              <Tab.Pane eventKey="opcionales">
                {/* Renderiza la sección de opcionales */}
                <OptionalsSection
                    data={tableData} 
                    itinerary={itinerary}
                    idGroup={id}
                    
                />
              </Tab.Pane>
              <Tab.Pane eventKey="liquidacion">
                <p>Contenido de liquidación no implementado aún.</p>
              </Tab.Pane>
            </Tab.Content>
          </Tab.Container>
        </Col>
      </Row>

      {/* Aquí incluirías los modales */}
      <EditGuideModal show={showEditGuideModal} onHide={() => setShowEditGuideModal(false)} groupData={groupData} onGuideUpdated={(newGuide) => {
        setGroupData({...groupData, guide_name: newGuide.name});
      }} />
      <EditBusModal show={showEditBusModal} onHide={() => setShowEditBusModal(false)} groupData={groupData} onBusUpdated={(updatedBus) => {
        setGroupData({...groupData, bus_company: updatedBus.company_name, bus_code: updatedBus.bus_code});
      }} />
      <EditQRModal show={showEditQRModal} onHide={() => setShowEditQRModal(false)} groupData={groupData} onQRUpdated={(newQR) => {
        setGroupData({...groupData, QR: newQR});
      }} />
      <EditOperationsAgentModal
      show={showEditOperationsAgentModal}
      onHide={() => setShowEditOperationsAgentModal(false)}
      groupData={groupData}
      onOperationsUpdated={(updatedOperations) => {
        setGroupData({ ...groupData, id_operations: updatedOperations.id, operaciones_name: updatedOperations.name });
      }}
    />
    <EditAssistantModal
      show={showEditAssistantModal}
      onHide={() => setShowEditAssistantModal(false)}
      groupData={groupData}
      onAssistantUpdated={(updatedAssistant) => {
        setGroupData({ ...groupData, id_assistant: updatedAssistant.id, nombre_asistente: updatedAssistant.name });
      }}
    />
    <EditResponsibleHotelsModal
      show={showEditResponsibleHotelsModal}
      onHide={() => setShowEditResponsibleHotelsModal(false)}
      groupData={groupData}
      onResponsibleUpdated={(updatedResponsible) => {
        setGroupData({ ...groupData, id_responsible_hotels: updatedResponsible.name});
      }}
    />
    </Container>
  );
};

export default GroupDetail;
