import React, { useState, useEffect } from 'react';
import { Modal, Button, Form, Row, Col } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';

const OptionalsFilterModal = ({ show, onHide, idGroup, current_table, onFiltersApplied }) => {
  const navigate = useNavigate();

  // Estados para cada filtro
  const [selectedPassengers, setSelectedPassengers] = useState([]);
  const [selectedCities, setSelectedCities] = useState([]);
  const [selectedActivities, setSelectedActivities] = useState([]);
  const [filterMinAge, setFilterMinAge] = useState('');
  const [filterMaxAge, setFilterMaxAge] = useState('');
  const [filterSex, setFilterSex] = useState('');
  const [filterPlaceOfPurchase, setFilterPlaceOfPurchase] = useState('');
  const [filterPaymentMethod, setFilterPaymentMethod] = useState('');

  // Datos para los select/checkboxes (se pueden cargar desde la API o recibir por props)
  const [passengers, setPassengers] = useState([]);
  const [cities, setCities] = useState([]);
  const [activities, setActivities] = useState([]);

  useEffect(() => {
    // Ejemplo: obtener pasajeros del grupo
    fetch(`${process.env.REACT_APP_API_URL}/clients/clents_group?id_group=${encodeURIComponent(idGroup)}`)
      .then(r => r.json())
      .then(data => setPassengers(data))
      .catch(err => console.error('Error al cargar pasajeros:', err));

    // Ejemplo: obtener ciudades para el filtro
    fetch(`${process.env.REACT_APP_API_URL}/days/get_days_for_filter?id_group=${encodeURIComponent(idGroup)}`)
      .then(r => r.json())
      .then(data => setCities(data))
      .catch(err => console.error('Error al cargar ciudades:', err));

    // Ejemplo: obtener actividades para el grupo
    fetch(`${process.env.REACT_APP_API_URL}/activity/activity_by_id_group?id_group=${encodeURIComponent(idGroup)}`)
      .then(r => r.json())
      .then(data => setActivities(data))
      .catch(err => console.error('Error al cargar actividades:', err));
  }, [idGroup]);

  const handleApplyFilter = () => {
    let filters = {
      passengers: selectedPassengers.length > 0 ? selectedPassengers : undefined,
      min_age: filterMinAge || undefined,
      max_age: filterMaxAge || undefined,
      sex: filterSex || undefined,
      city: selectedCities.length > 0 ? selectedCities : undefined,
      activity_id: selectedActivities.length > 0 ? selectedActivities : undefined,
      'place_of_purchase': filterPlaceOfPurchase || undefined,
      'payment_method': filterPaymentMethod || undefined,
    };

    // Eliminar campos undefined
    Object.keys(filters).forEach(key => {
      if (filters[key] === undefined) {
        delete filters[key];
      }
    });

    const filtersParam = encodeURIComponent(JSON.stringify(filters));

    // Si se proporciona un callback, lo usamos; de lo contrario navegamos
    if (onFiltersApplied) {
      onFiltersApplied(filters);
    } else {
      navigate(`/grupo/${idGroup}?table=${current_table}&filters=${filtersParam}`);
    }
    onHide();
  };

  return (
    <Modal show={show} onHide={onHide} size="lg" centered>
      <Modal.Header closeButton>
        <Modal.Title>Filtrar Opcionales</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <Row className="mb-3">
          <Col md={8}>
            <Form.Group>
              <Form.Label>Pasajeros</Form.Label>
              <div>
                {passengers.map(p => {
                  const fullName = [p.first_name, p.second_name, p.paternal_surname, p.mother_surname]
                    .filter(Boolean)
                    .join(' ');
                  return (
                    <Form.Check
                      key={p.id_clients}
                      type="checkbox"
                      id={`passenger_${p.id_clients}`}
                      label={fullName}
                      value={p.id_clients}
                      checked={selectedPassengers.includes(p.id_clients)}
                      onChange={(e) => {
                        if (e.target.checked) {
                          setSelectedPassengers(prev => [...prev, p.id_clients]);
                        } else {
                          setSelectedPassengers(prev => prev.filter(id => id !== p.id_clients));
                        }
                      }}
                    />
                  );
                })}
              </div>
              <Form.Text className="text-muted">
                Seleccione uno o varios pasajeros marcando las casillas.
              </Form.Text>
            </Form.Group>
          </Col>
          <Col md={4}>
            <Form.Group controlId="filterSex">
              <Form.Label>Sexo</Form.Label>
              <Form.Select value={filterSex} onChange={(e) => setFilterSex(e.target.value)}>
                <option value="">Todos</option>
                <option value="M">Masculino</option>
                <option value="F">Femenino</option>
                <option value="O">Otro</option>
              </Form.Select>
            </Form.Group>
          </Col>
        </Row>
        <Row className="mb-3">
          <Col md={4}>
            <Form.Group controlId="filterMinAge">
              <Form.Label>Edad Mínima</Form.Label>
              <Form.Control
                type="number"
                min="0"
                value={filterMinAge}
                onChange={(e) => setFilterMinAge(e.target.value)}
              />
            </Form.Group>
          </Col>
          <Col md={4}>
            <Form.Group controlId="filterMaxAge">
              <Form.Label>Edad Máxima</Form.Label>
              <Form.Control
                type="number"
                min="0"
                value={filterMaxAge}
                onChange={(e) => setFilterMaxAge(e.target.value)}
              />
            </Form.Group>
          </Col>
          <Col md={4}>
            <Form.Group>
              <Form.Label>Ciudades</Form.Label>
              <div>
                {cities.map(cityName => (
                  <Form.Check
                    key={cityName}
                    type="checkbox"
                    id={`city_${cityName}`}
                    label={cityName}
                    value={cityName}
                    checked={selectedCities.includes(cityName)}
                    onChange={(e) => {
                      if (e.target.checked) {
                        setSelectedCities(prev => [...prev, cityName]);
                      } else {
                        setSelectedCities(prev => prev.filter(c => c !== cityName));
                      }
                    }}
                  />
                ))}
              </div>
              <Form.Text className="text-muted">
                Seleccione una o varias ciudades.
              </Form.Text>
            </Form.Group>
          </Col>
        </Row>
        <Row className="mb-3">
          <Col>
            <Form.Group>
              <Form.Label>Actividades</Form.Label>
              <div>
                {activities.map(activity => (
                  <Form.Check
                    key={activity.Activity.id_optional}
                    type="checkbox"
                    id={`activity_${activity.Activity.id_optional}`}
                    label={activity.name}
                    value={activity.Activity.id_optional}
                    checked={selectedActivities.includes(activity.Activity.id_optional)}
                    onChange={(e) => {
                      if (e.target.checked) {
                        setSelectedActivities(prev => [...prev, activity.Activity.id_optional]);
                      } else {
                        setSelectedActivities(prev => prev.filter(a => a !== activity.Activity.id_optional));
                      }
                    }}
                  />
                ))}
              </div>
              <Form.Text className="text-muted">
                Seleccione una o varias actividades.
              </Form.Text>
            </Form.Group>
          </Col>
        </Row>
        <Row className="mb-3">
          <Col md={6}>
            <Form.Group controlId="filterPlaceOfPurchase">
              <Form.Label>Lugar de compra</Form.Label>
              <Form.Select
                value={filterPlaceOfPurchase}
                onChange={(e) => setFilterPlaceOfPurchase(e.target.value)}
              >
                <option value="">Todos</option>
                <option value="before_trip">Antes del viaje</option>
                <option value="during_trip">Durante el viaje</option>
              </Form.Select>
            </Form.Group>
          </Col>
          <Col md={6}>
            <Form.Group controlId="filterPaymentMethod">
              <Form.Label>Método de pago</Form.Label>
              <Form.Select
                value={filterPaymentMethod}
                onChange={(e) => setFilterPaymentMethod(e.target.value)}
              >
                <option value="">Todos</option>
                <option value="credit_card">Tarjeta de crédito</option>
                <option value="debit_card">Tarjeta de débito</option>
                <option value="cash">Efectivo</option>
              </Form.Select>
            </Form.Group>
          </Col>
        </Row>
      </Modal.Body>
      <Modal.Footer>
        <Button variant="secondary" onClick={onHide}>Cancelar</Button>
        <Button variant="primary" onClick={handleApplyFilter}>Aplicar Filtro</Button>
      </Modal.Footer>
    </Modal>
  );
};

export default OptionalsFilterModal;
