import React, { useState, useEffect } from 'react';
import { Modal, Button, Form, Row, Col, Alert } from 'react-bootstrap';

// Función auxiliar para formatear una fecha al formato "YYYY-MM-DD"
const formatDateForInput = (dateString) => {
  if (!dateString) return '';
  const dateObj = new Date(dateString);
  if (isNaN(dateObj.getTime())) return '';
  return dateObj.toISOString().split('T')[0];
};

/**
 * HotelModal permite agregar o editar una asignación de hotel.
 * 
 * Props:
 * - show: Booleano para mostrar/ocultar el modal.
 * - onHide: Función para ocultar el modal.
 * - initialData: Datos iniciales de la asignación (modo edición). En modo agregar, suele ser {}.
 * - onSave: Callback que se invoca al enviar el formulario, pasando el payload para actualizar (PUT).
 * - onAddAnother: Callback que se invoca cuando se quiere agregar otro hotel para el mismo día.
 * - groupPax: Número total de pasajeros del grupo (para validar si se pueden agregar más asignaciones).
 * - availableHotels: (Opcional) Lista de hoteles disponibles para la ciudad.
 *   Si no se pasa, se hará el GET al endpoint.
 */
const HotelModal = ({ 
  show, 
  onHide, 
  initialData = {}, 
  onSave, 
  onAddAnother, 
  groupPax, 
  availableHotels: propAvailableHotels 
}) => {
  // Estados para cada campo; en modo edición se pre-cargan desde initialData.
  // Los campos "city" y "date" se muestran pero se bloquean (readOnly) para que no se modifiquen.
  const [city, setCity] = useState(initialData.city || '');
  const [date, setDate] = useState(formatDateForInput(initialData.date));
  // En lugar de hotelName, usaremos un dropdown para seleccionar un hotel.
  const [hotelId, setHotelId] = useState(initialData.id_hotel || ''); // se espera que sea un número o cadena
  const [hotelsList, setHotelsList] = useState(propAvailableHotels || []);  
  const [checkIn, setCheckIn] = useState(initialData.check_in || '');
  const [checkOut, setCheckOut] = useState(initialData.check_out || '');
  const [roomingList, setRoomingList] = useState(
    typeof initialData.rooming_list === 'boolean' ? initialData.rooming_list : false
  );
  const [proForma, setProForma] = useState(
    typeof initialData.pro_forma === 'boolean' ? initialData.pro_forma : false
  );
  const [currency, setCurrency] = useState(initialData.currency || 'USD');
  const [totalToPay, setTotalToPay] = useState(
    initialData.total_to_pay != null ? initialData.total_to_pay : 0
  );
  const [paymentDate, setPaymentDate] = useState(formatDateForInput(initialData.payment_date));
  const [paymentDoneDate, setPaymentDoneDate] = useState(formatDateForInput(initialData.payment_done_date));
  const [factura, setFactura] = useState(
    typeof initialData.factura === 'boolean' ? initialData.factura : false
  );
  const [iga, setIga] = useState(
    typeof initialData.iga === 'boolean' ? initialData.iga : false
  );
  // El campo para comentarios ahora se llamará "comment" y, por el workflow, se inicia vacío
  const [comment, setComment] = useState('');

  // Estado para el número de pasajeros asignados en esta asignación.
  // Se asume que en initialData viene el campo "pax" o "total_assigned"
  const [paxAssigned, setPaxAssigned] = useState(initialData.pax || 0);

  // Estado para mostrar mensajes de validación
  const [validationMsg, setValidationMsg] = useState('');

  // Cada vez que initialData cambie, actualizamos los estados (menos el comentario, que se inicia vacío).
  useEffect(() => {
    setCity(initialData.city || '');
    setDate(formatDateForInput(initialData.date));
    // Para el hotel, si en edición ya existe un id, se usa; de lo contrario, se deja vacío.
    setHotelId(initialData.id_hotel || '');
    // Si el prop de hoteles disponibles no viene, hacemos el GET a /hotels/get_hotel_by_city?city=
    if (!propAvailableHotels && initialData.city) {
      fetch(`${process.env.REACT_APP_API_URL}/hotels/get_hotel_by_city?city=${encodeURIComponent(initialData.city)}`)
        .then(res => res.json())
        .then(data => {
          // Se espera que data sea un array de hoteles: [{ id: 1, name: 'Hotel X' }, ...]
          setHotelsList(data);
        })
        .catch(err => console.error('Error al obtener hoteles:', err));
    }
    setCheckIn(initialData.check_in || '');
    setCheckOut(initialData.check_out || '');
    setPaxAssigned(initialData.pax || 0);
    setRoomingList(typeof initialData.rooming_list === 'boolean' ? initialData.rooming_list : false);
    setProForma(typeof initialData.pro_forma === 'boolean' ? initialData.pro_forma : false);
    setCurrency(initialData.currency || 'USD');
    setTotalToPay(initialData.total_to_pay != null ? initialData.total_to_pay : 0);
    setPaymentDate(formatDateForInput(initialData.payment_date));
    setPaymentDoneDate(formatDateForInput(initialData.payment_done_date));
    setFactura(typeof initialData.factura === 'boolean' ? initialData.factura : false);
    setIga(typeof initialData.iga === 'boolean' ? initialData.iga : false);
    // No se precarga "comment" para que siempre inicie en blanco.
  }, [initialData, propAvailableHotels]);

  // Efecto para actualizar automáticamente checkIn y checkOut cuando cambia la fecha.
  useEffect(() => {
    // Si se cambia la fecha (readOnly, pero viene de initialData) actualizamos checkIn y checkOut.
    if (date) {
      setCheckIn(date); // Por defecto, checkIn es igual a la fecha.
      // Para checkOut, sumamos un día.
      const dt = new Date(date);
      dt.setDate(dt.getDate() + 1);
      setCheckOut(dt.toISOString().split('T')[0]);
    }
  }, [date]);

  // Calcula si se puede agregar otro hotel para el mismo día (paxAssigned < groupPax)
  const canAddAnother = paxAssigned < groupPax;

  // Maneja el envío del formulario.
  const handleSubmit = (e) => {
    e.preventDefault();
    // Preparamos el payload de acuerdo al modelo HotelReservationUpdate.
    // Validar que paxAssigned sea mayor que 0.
    if (parseInt(paxAssigned, 10) === 0) {
      setValidationMsg('El número de PAX asignados debe ser mayor que 0.');
      return;
    }
    const payload = {
      id: initialData.assignment_id || '', // Se envía si es edición.
      id_hotel: parseInt(hotelId, 10), // Convertir a número
      id_group: initialData.id_group || '', // Se espera que esté en initialData o se extraiga de otro lugar
      start_date: date, // Asumimos que el backend acepta fecha en formato "YYYY-MM-DD"
      end_date: date,   // Si la asignación es por día, se usa la misma fecha en start y end.
      pax: parseInt(paxAssigned, 10),
      currency,
      total_to_pay: parseFloat(totalToPay),
      comment, // El nuevo comentario ingresado.
      rooming_list: roomingList,
      pro_forma: proForma,
      payment_date: paymentDate,
      payment_done_date: paymentDoneDate,
      payed_by: null, // O el valor que corresponda si se requiere.
      factura,
      iga,
    };
    onSave(payload);
  };

  return (
    <Modal show={show} onHide={onHide} centered size="lg">
      <Form onSubmit={handleSubmit}>
        <Modal.Header closeButton>
          <Modal.Title>
            {initialData.assignment_id ? 'Editar Asignación de Hotel' : 'Agregar Asignación de Hotel'}
          </Modal.Title>
        </Modal.Header>
        <Modal.Body>
          {/* Fila 1: Ciudad y Fecha (no editables) */}
          <Row className="mb-3">
            <Col md={6}>
              <Form.Group controlId="formCity">
                <Form.Label>Ciudad</Form.Label>
                <Form.Control
                  type="text"
                  value={city}
                  readOnly
                />
              </Form.Group>
            </Col>
            <Col md={6}>
              <Form.Group controlId="formDate">
                <Form.Label>Fecha</Form.Label>
                <Form.Control
                  type="date"
                  value={date}
                  readOnly
                />
              </Form.Group>
            </Col>
          </Row>
          {/* Fila 2: Selección de Hotel mediante Dropdown */}
          <Form.Group controlId="formHotel">
            <Form.Label>Hotel</Form.Label>
            <Form.Select 
              value={hotelId}
              onChange={(e) => setHotelId(e.target.value)}
              required
            >
              <option value="">Seleccione un hotel</option>
              {hotelsList.map((hotel) => (
                <option key={hotel.id_hotel} value={hotel.id_hotel}>
                  {hotel.hotel_name}
                </option>
              ))}
            </Form.Select>
          </Form.Group>
          {/* Fila 3: Check-in y Check-out */}
          <Row className="mb-3">
            <Col md={6}>
              <Form.Group controlId="formCheckIn">
                <Form.Label>Check-in</Form.Label>
                <Form.Control
                  type="datetime-local"
                  value={checkIn}
                  onChange={(e) => setCheckIn(e.target.value)}
                  required
                />
              </Form.Group>
            </Col>
            <Col md={6}>
              <Form.Group controlId="formCheckOut">
                <Form.Label>Check-out</Form.Label>
                <Form.Control
                  type="datetime-local"
                  value={checkOut}
                  onChange={(e) => setCheckOut(e.target.value)}
                  required
                />
              </Form.Group>
            </Col>
          </Row>
          {/* Fila 4: Switches para Rooming List y Pro Forma */}
          <Row className="mb-3">
            <Col md={4}>
              <Form.Check
                type="switch"
                id="formRoomingList"
                label="Rooming List Enviado"
                checked={roomingList}
                onChange={(e) => setRoomingList(e.target.checked)}
              />
            </Col>
            <Col md={4}>
              <Form.Check
                type="switch"
                id="formProForma"
                label="Pro Forma Enviado"
                checked={proForma}
                onChange={(e) => setProForma(e.target.checked)}
              />
            </Col>
          </Row>
          {/* Fila 5: Moneda, Total a Pagar y PAX asignados */}
          <Row className="mb-3">
            <Col md={4}>
              <Form.Group controlId="formCurrency">
                <Form.Label>Moneda</Form.Label>
                <Form.Select value={currency} onChange={(e) => setCurrency(e.target.value)}>
                  <option value="USD">USD</option>
                  <option value="EUR">EUR</option>
                  <option value="ARS">ARS</option>
                </Form.Select>
              </Form.Group>
            </Col>
            <Col md={4}>
              <Form.Group controlId="formTotalToPay">
                <Form.Label>Total a Pagar</Form.Label>
                <Form.Control
                  type="number"
                  value={totalToPay}
                  onChange={(e) => setTotalToPay(e.target.value)}
                  required
                />
              </Form.Group>
            </Col>
            <Col md={4}>
              <Form.Group controlId="formPAX">
                <Form.Label>PAX Asignados</Form.Label>
                <Form.Control
                  type="number"
                  value={paxAssigned}
                  onChange={(e) => setPaxAssigned(e.target.value)}
                  required
                />
              </Form.Group>
            </Col>
          </Row>
          {/* Fila 6: Fechas de Pago */}
          <Row className="mb-3">
            <Col md={6}>
              <Form.Group controlId="formPaymentDate">
                <Form.Label>Fecha de Pago</Form.Label>
                <Form.Control
                  type="date"
                  value={paymentDate}
                  onChange={(e) => setPaymentDate(e.target.value)}
                />
              </Form.Group>
            </Col>
            <Col md={6}>
              <Form.Group controlId="formPaymentDoneDate">
                <Form.Label>Fecha de Pago Realizado</Form.Label>
                <Form.Control
                  type="date"
                  value={paymentDoneDate}
                  onChange={(e) => setPaymentDoneDate(e.target.value)}
                />
              </Form.Group>
            </Col>
          </Row>
          {/* Fila 7: Factura e IGA */}
          <Row className="mb-3">
            <Col md={6}>
              <Form.Check
                type="switch"
                id="formFactura"
                label="Factura Emitida"
                checked={factura}
                onChange={(e) => setFactura(e.target.checked)}
              />
            </Col>
            <Col md={6}>
              <Form.Check
                type="switch"
                id="formIGA"
                label="IGA Gestionado"
                checked={iga}
                onChange={(e) => setIga(e.target.checked)}
              />
            </Col>
          </Row>
          {/* Fila 8: Comentarios */}
          <Form.Group controlId="formNotes" className="mb-3">
            <Form.Label>Comentarios</Form.Label>
            <Form.Control
              as="textarea"
              rows={3}
              placeholder="Escribe aquí un nuevo comentario. Los comentarios anteriores no se podrán modificar."
              value={comment}
              onChange={(e) => setComment(e.target.value)}
            />
          </Form.Group>
        </Modal.Body>
        <Modal.Footer className="d-flex justify-content-between">
          <div>
            <Button variant="secondary" onClick={onHide}>
              Cancelar
            </Button>
            <Button variant="primary" type="submit" className="ms-2">
              Guardar Cambios
            </Button>
          </div>
          {/* Botón para agregar otra asignación si aún faltan pasajeros */}
          {canAddAnother && (
            <Button variant="outline-primary" onClick={() => {
              // Primero se guarda la asignación actual
              handleSubmit(new Event('submit'));
              // Luego se llama al callback para agregar otra asignación para el mismo día.
              if (onAddAnother) onAddAnother();
            }}>
              Agregar otro hotel para este día
            </Button>
          )}
        </Modal.Footer>
      </Form>
    </Modal>
  );
};

export default HotelModal;
