import React, { useState, useEffect } from 'react';
import { Modal, Button, Form, Row, Col } from 'react-bootstrap';

const AddOptionalModal = ({ 
  show, 
  onHide, 
  clientId, 
  groupId, 
  city, 
  cityDays, 
  clientName, 
  onOptionalAdded,
  clientAge // para determinar el precio según edad, si se requiere
}) => {
  // Estados para el día seleccionado y para los opcionales disponibles
  const [selectedDayId, setSelectedDayId] = useState('');
  const [availableOptionals, setAvailableOptionals] = useState([]);
  const [selectedOptionalId, setSelectedOptionalId] = useState('');
  
  // Estados para el formulario
  const [price, setPrice] = useState(0);
  const [discount, setDiscount] = useState(0);
  const [placeOfPurchase, setPlaceOfPurchase] = useState('before_trip');
  const [paymentMethod, setPaymentMethod] = useState('credit_card');
  const [total, setTotal] = useState(0);

  // Al abrir el modal, si hay días, seleccionar el primero
  useEffect(() => {
    if (show && cityDays && cityDays.length > 0) {
      setSelectedDayId(cityDays[0].id);
    }
  }, [show, cityDays]);

  // Cuando cambia el día seleccionado, se cargan los opcionales disponibles para ese día
  useEffect(() => {
    if (selectedDayId) {
      fetch(`${process.env.REACT_APP_API_URL}/optionals_purchase?id_group=${groupId}&id_days=${selectedDayId}`)
        .then(res => res.json())
        .then(data => {
          // Filtramos opcionales válidos (que tengan id_optional y name)
          const valid = (data.optionals || []).filter(opt => opt.id_optional && opt.name);
          setAvailableOptionals(valid);
          // Preseleccionamos el primer opcional, si existe
          if (valid.length > 0) {
            setSelectedOptionalId(valid[0].id_optional.toString());
            // Pre-cargar precio según edad: si menor de 12 usar minor_price, sino adult_price
            const prePrice = clientAge && clientAge < 12 ? valid[0].minor_price : valid[0].adult_price;
            setPrice(prePrice || 0);
          } else {
            setSelectedOptionalId('');
            setPrice(0);
          }
        })
        .catch(error => {
          console.error('Error al cargar opcionales:', error);
          setAvailableOptionals([]);
        });
    }
  }, [selectedDayId, groupId, clientAge]);

  // Calcular total cada vez que precio o descuento cambian
  useEffect(() => {
    const computedTotal = price - (price * (discount / 100));
    setTotal(computedTotal);
  }, [price, discount]);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!selectedOptionalId) {
      alert('Debe seleccionar una actividad opcional.');
      return;
    }
    const payload = {
      client_id: clientId,
      id_group: groupId,
      id_days: selectedDayId,
      id_activity: parseInt(selectedOptionalId, 10), // asumimos que el id_activity coincide con el id_optional aquí; ajustar según la API
      price: parseFloat(price),
      discount: discount.toString(),
      total: total,
      place_of_purchase: placeOfPurchase,
      payment_method: paymentMethod,
      source: 'admin'
    };
    fetch(`${process.env.REACT_APP_API_URL}/optionals_purchase`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(payload)
    })
      .then(res => res.json())
      .then(data => {
        if (data.status === 'success') {
          onOptionalAdded(data);
          onHide();
        } else {
          alert('Error al agregar opcional: ' + (data.message || 'Error desconocido'));
        }
      })
      .catch(error => {
        console.error('Error al agregar opcional:', error);
        alert('Error al agregar opcional.');
      });
  };

  return (
    <Modal show={show} onHide={onHide} centered size="lg">
      <Form onSubmit={handleSubmit}>
        <Modal.Header closeButton>
          <Modal.Title>
            Agregar opcionales para: <span style={{ textDecoration: 'underline' }}>{city}</span>, Cliente: <span style={{ textDecoration: 'underline' }}>{clientName}</span>
          </Modal.Title>
        </Modal.Header>
        <Modal.Body>
          {/* Botones para elegir el día */}
          <Row className="mb-3">
            <Col>
              <div className="btn-group" role="group" aria-label="Días disponibles">
                {cityDays.map((day, idx) => (
                  <Button
                    key={day.id}
                    variant={day.id === selectedDayId ? 'primary' : 'outline-primary'}
                    onClick={() => setSelectedDayId(day.id)}
                  >
                    {day.date}
                  </Button>
                ))}
              </div>
            </Col>
          </Row>
          {/* Opcionales disponibles */}
          <Row className="mb-3">
            <Col>
              <Form.Group controlId="optionalSelect">
                <Form.Label>Actividades Opcionales</Form.Label>
                <Form.Select
                  value={selectedOptionalId}
                  onChange={(e) => {
                    setSelectedOptionalId(e.target.value);
                    // Buscar el opcional seleccionado para actualizar el precio
                    const opt = availableOptionals.find(o => o.id_optional.toString() === e.target.value);
                    if (opt) {
                      const newPrice = clientAge && clientAge < 12 ? opt.minor_price : opt.adult_price;
                      setPrice(newPrice || 0);
                    }
                  }}
                  required
                >
                  <option value="">Seleccione...</option>
                  {availableOptionals.map(opt => (
                    <option key={opt.id_optional} value={opt.id_optional}>
                      {opt.name}
                    </option>
                  ))}
                </Form.Select>
              </Form.Group>
            </Col>
          </Row>
          {/* Campos para precio, descuento, lugar de compra, método de pago y total */}
          <Row className="mb-3">
            <Col md={6}>
              <Form.Group controlId="priceInput">
                <Form.Label>Precio</Form.Label>
                <Form.Control
                  type="number"
                  value={price}
                  onChange={(e) => setPrice(e.target.value)}
                  required
                />
              </Form.Group>
            </Col>
            <Col md={6}>
              <Form.Group controlId="discountInput">
                <Form.Label>Descuento (%)</Form.Label>
                <Form.Range
                  min="0"
                  max="100"
                  value={discount}
                  onChange={(e) => setDiscount(e.target.value)}
                />
                <div>{discount}%</div>
              </Form.Group>
            </Col>
          </Row>
          <Row className="mb-3">
            <Col md={6}>
              <Form.Group controlId="placeOfPurchase">
                <Form.Label>Lugar de compra</Form.Label>
                <Form.Select
                  value={placeOfPurchase}
                  onChange={(e) => setPlaceOfPurchase(e.target.value)}
                  required
                >
                  <option value="before_trip">Antes del viaje</option>
                  <option value="during_trip">Durante el viaje</option>
                </Form.Select>
              </Form.Group>
            </Col>
            <Col md={6}>
              <Form.Group controlId="paymentMethod">
                <Form.Label>Método de pago</Form.Label>
                <Form.Select
                  value={paymentMethod}
                  onChange={(e) => setPaymentMethod(e.target.value)}
                  required
                >
                  <option value="credit_card">Tarjeta de crédito</option>
                  <option value="debit_card">Tarjeta de débito</option>
                  <option value="cash">Efectivo</option>
                </Form.Select>
              </Form.Group>
            </Col>
          </Row>
          <Row className="mb-3">
            <Col>
              <Form.Group controlId="totalDisplay">
                <Form.Label>Total</Form.Label>
                <Form.Control type="text" value={`${total.toFixed(2)} €`} readOnly />
              </Form.Group>
            </Col>
          </Row>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={onHide}>Cancelar</Button>
          <Button variant="primary" type="submit">Guardar</Button>
        </Modal.Footer>
      </Form>
    </Modal>
  );
};

export default AddOptionalModal;
