import React, { useState } from "react";
// Importa BrowserRouter, Routes y Route para la navegación
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";

// Importa las páginas que vamos a crear
import Login from "./pages/Login";
import Home from "./pages/Home";
//import Groups from "./pages/Groups";
//import GroupDetail from "./pages/GroupDetail";

// Layout que incluye Header y Sidebar
import Layout from "./components/Layout";

const App = () => {
  // Estado para simular la autenticación. (En el futuro se reemplaza por un sistema real.)
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  return (
    <Router>
      <Routes>
        {/* Ruta de Login */}
        <Route
          path="/login"
          element={<Login setIsAuthenticated={setIsAuthenticated} />}
        />

        {/* Las rutas protegidas van a estar dentro del Layout */}
        <Route
          path="/"
          element={
            isAuthenticated ? <Layout /> : <Navigate to="/login" replace />
          }
        >
          {/* Página principal */}
          <Route index element={<Home />} />

          //{/* Sección de grupos */}
          <Route path="grupos" element={<Groups />} />

          {/* Detalle de un grupo (ruta dinámica) */}
          <Route path="grupo/:id" element={<GroupDetail />} />
          {/* Puedes agregar otras rutas como clientes, itinerario, etc. */}
        </Route>

        {/* Si se ingresa a una ruta desconocida redireccionamos al login */}
        <Route path="*" element={<Navigate to="/login" replace />} />
      </Routes>
    </Router>
  );
};

export default App;
