import React, { useState, useEffect } from 'react';
import PropTypes from 'prop-types';
import { Modal, Button, Form, Row, Col } from 'react-bootstrap';

/**
 * HotelFilterModal permite filtrar la tabla de asignaciones de hoteles.
 *
 * Las opciones de filtro incluyen:
 * - Ciudad (select)
 * - Hotel (select)
 * - Divisa (select)
 * - Fecha (input date + operador: Igual, Mayor que, Menor que)
 * - Payment Date (input date + operador)
 * - Payment Done Date (input date + operador)
 * - PAX (input numérico)
 * - Rooming List, Pro Forma, Factura e IGA (select: Todos, Sí, No)
 * - Pagado por (input o select)
 *
 * Al aplicar el filtro se arma un objeto de filtros para enviarlo al backend.
 */
const HotelFilterModal = ({ show, onHide, onApplyFilters }) => {
  // Opciones de ejemplo para los desplegables; se pueden reemplazar por fetch si se requiere
  const cityOptions = ['Todos', 'CAMBRIDGE', 'DURHAM', 'EDIMBURGO', 'GLASGOW', 'DUBLIN'];
  const hotelOptions = ['Todos', 'Best Western Glasgow Hotel', 'Maldron Hotel Tallaght'];
  const currencyOptions = ['Todos', 'EUR', 'USD', 'CHF', 'GBP'];

  // Estado para cada filtro
  const [city, setCity] = useState('Todos');
  const [hotel, setHotel] = useState('Todos');
  const [currency, setCurrency] = useState('Todos');
  // Para las fechas, usamos dos estados: operador y valor
  const [dateOp, setDateOp] = useState('Igual');
  const [dateValue, setDateValue] = useState('');
  const [paymentDateOp, setPaymentDateOp] = useState('Igual');
  const [paymentDateValue, setPaymentDateValue] = useState('');
  const [paymentDoneDateOp, setPaymentDoneDateOp] = useState('Igual');
  const [paymentDoneDateValue, setPaymentDoneDateValue] = useState('');
  const [pax, setPax] = useState('');
  const [roomingList, setRoomingList] = useState('Todos');
  const [proForma, setProForma] = useState('Todos');
  const [factura, setFactura] = useState('Todos');
  const [iga, setIga] = useState('Todos');
  const [payedBy, setPayedBy] = useState('');

  // Función para limpiar todos los filtros
  const handleClear = () => {
    setCity('Todos');
    setHotel('Todos');
    setCurrency('Todos');
    setDateOp('Igual');
    setDateValue('');
    setPaymentDateOp('Igual');
    setPaymentDateValue('');
    setPaymentDoneDateOp('Igual');
    setPaymentDoneDateValue('');
    setPax('');
    setRoomingList('Todos');
    setProForma('Todos');
    setFactura('Todos');
    setIga('Todos');
    setPayedBy('');
  };

  // Al enviar, se arma un objeto de filtros
  const handleSubmit = (e) => {
    e.preventDefault();
    // Se construye el objeto de filtros
    const filters = {};
    if (city && city !== 'Todos') filters.city = city;
    if (hotel && hotel !== 'Todos') filters.hotel = hotel;
    if (currency && currency !== 'Todos') filters.currency = currency;
    if (dateValue) filters.date = { op: dateOp, value: dateValue };
    if (paymentDateValue) filters.payment_date = { op: paymentDateOp, value: paymentDateValue };
    if (paymentDoneDateValue) filters.payment_done_date = { op: paymentDoneDateOp, value: paymentDoneDateValue };
    if (pax) filters.pax = pax;
    if (roomingList && roomingList !== 'Todos') filters.rooming_list = roomingList === 'Sí';
    if (proForma && proForma !== 'Todos') filters.pro_forma = proForma === 'Sí';
    if (factura && factura !== 'Todos') filters.factura = factura === 'Sí';
    if (iga && iga !== 'Todos') filters.iga = iga === 'Sí';
    if (payedBy) filters.payed_by = payedBy;

    onApplyFilters(filters);
    onHide();
  };

  return (
    <Modal show={show} onHide={onHide} centered size="lg">
      <Form onSubmit={handleSubmit}>
        <Modal.Header closeButton>
          <Modal.Title>Filtrar Asignaciones de Hoteles</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Row>
            <Col md={4}>
              <Form.Group controlId="filterCity" className="mb-3">
                <Form.Label>Ciudad</Form.Label>
                <Form.Select value={city} onChange={(e) => setCity(e.target.value)}>
                  {cityOptions.map((opt) => (
                    <option key={opt} value={opt}>{opt}</option>
                  ))}
                </Form.Select>
              </Form.Group>
            </Col>
            <Col md={4}>
              <Form.Group controlId="filterHotel" className="mb-3">
                <Form.Label>Hotel</Form.Label>
                <Form.Select value={hotel} onChange={(e) => setHotel(e.target.value)}>
                  {hotelOptions.map((opt) => (
                    <option key={opt} value={opt}>{opt}</option>
                  ))}
                </Form.Select>
              </Form.Group>
            </Col>
            <Col md={4}>
              <Form.Group controlId="filterCurrency" className="mb-3">
                <Form.Label>Divisa</Form.Label>
                <Form.Select value={currency} onChange={(e) => setCurrency(e.target.value)}>
                  {currencyOptions.map((opt) => (
                    <option key={opt} value={opt}>{opt}</option>
                  ))}
                </Form.Select>
              </Form.Group>
            </Col>
          </Row>
          <Row>
            <Col md={4}>
              <Form.Group controlId="filterDate" className="mb-3">
                <Form.Label>Fecha</Form.Label>
                <Row>
                  <Col xs={4}>
                    <Form.Select value={dateOp} onChange={(e) => setDateOp(e.target.value)}>
                      <option value="Igual">Igual</option>
                      <option value=">">Mayor que</option>
                      <option value="<">Menor que</option>
                    </Form.Select>
                  </Col>
                  <Col xs={8}>
                    <Form.Control
                      type="date"
                      value={dateValue}
                      onChange={(e) => setDateValue(e.target.value)}
                    />
                  </Col>
                </Row>
              </Form.Group>
            </Col>
            <Col md={4}>
              <Form.Group controlId="filterPaymentDate" className="mb-3">
                <Form.Label>Fecha de Pago</Form.Label>
                <Row>
                  <Col xs={4}>
                    <Form.Select value={paymentDateOp} onChange={(e) => setPaymentDateOp(e.target.value)}>
                      <option value="Igual">Igual</option>
                      <option value=">">Mayor que</option>
                      <option value="<">Menor que</option>
                    </Form.Select>
                  </Col>
                  <Col xs={8}>
                    <Form.Control
                      type="date"
                      value={paymentDateValue}
                      onChange={(e) => setPaymentDateValue(e.target.value)}
                    />
                  </Col>
                </Row>
              </Form.Group>
            </Col>
            <Col md={4}>
              <Form.Group controlId="filterPaymentDoneDate" className="mb-3">
                <Form.Label>Fecha de Pago Realizado</Form.Label>
                <Row>
                  <Col xs={4}>
                    <Form.Select value={paymentDoneDateOp} onChange={(e) => setPaymentDoneDateOp(e.target.value)}>
                      <option value="Igual">Igual</option>
                      <option value=">">Mayor que</option>
                      <option value="<">Menor que</option>
                    </Form.Select>
                  </Col>
                  <Col xs={8}>
                    <Form.Control
                      type="date"
                      value={paymentDoneDateValue}
                      onChange={(e) => setPaymentDoneDateValue(e.target.value)}
                    />
                  </Col>
                </Row>
              </Form.Group>
            </Col>
          </Row>
          <Row>
            <Col md={4}>
              <Form.Group controlId="filterPax" className="mb-3">
                <Form.Label>PAX</Form.Label>
                <Form.Control
                  type="number"
                  placeholder="Cantidad"
                  value={pax}
                  onChange={(e) => setPax(e.target.value)}
                />
              </Form.Group>
            </Col>
            <Col md={4}>
              <Form.Group controlId="filterRoomingList" className="mb-3">
                <Form.Label>Rooming List</Form.Label>
                <Form.Select value={roomingList} onChange={(e) => setRoomingList(e.target.value)}>
                  <option value="">Todos</option>
                  <option value="true">Sí</option>
                  <option value="false">No</option>
                </Form.Select>
              </Form.Group>
            </Col>
            <Col md={4}>
              <Form.Group controlId="filterProForma" className="mb-3">
                <Form.Label>Pro Forma</Form.Label>
                <Form.Select value={proForma} onChange={(e) => setProForma(e.target.value)}>
                  <option value="">Todos</option>
                  <option value="true">Sí</option>
                  <option value="false">No</option>
                </Form.Select>
              </Form.Group>
            </Col>
          </Row>
          <Row>
            <Col md={4}>
              <Form.Group controlId="filterFactura" className="mb-3">
                <Form.Label>Factura Emitida</Form.Label>
                <Form.Select value={factura} onChange={(e) => setFactura(e.target.value)}>
                  <option value="">Todos</option>
                  <option value="true">Sí</option>
                  <option value="false">No</option>
                </Form.Select>
              </Form.Group>
            </Col>
            <Col md={4}>
              <Form.Group controlId="filterIga" className="mb-3">
                <Form.Label>IGA Gestionado</Form.Label>
                <Form.Select value={iga} onChange={(e) => setIga(e.target.value)}>
                  <option value="">Todos</option>
                  <option value="true">Sí</option>
                  <option value="false">No</option>
                </Form.Select>
              </Form.Group>
            </Col>
            <Col md={4}>
              <Form.Group controlId="filterPayedBy" className="mb-3">
                <Form.Label>Pagado por</Form.Label>
                <Form.Control
                  type="text"
                  placeholder="Nombre"
                  value={payedBy}
                  onChange={(e) => setPayedBy(e.target.value)}
                />
              </Form.Group>
            </Col>
          </Row>
        </Modal.Body>
        <Modal.Footer className="d-flex justify-content-between">
          <Button variant="secondary" onClick={() => { handleClear(); onHide(); }}>
            Limpiar Filtros
          </Button>
          <Button variant="primary" type="submit">
            Aplicar Filtros
          </Button>
        </Modal.Footer>
      </Form>
    </Modal>
  );
};

HotelFilterModal.propTypes = {
  show: PropTypes.bool.isRequired,
  onHide: PropTypes.func.isRequired,
  onApplyFilters: PropTypes.func.isRequired,
};

export default HotelFilterModal;
