import React, { useState } from 'react';
import PropTypes from 'prop-types';
import { Modal, Button, Form, Row, Col } from 'react-bootstrap';

/**
 * HotelFilterModal permite filtrar la tabla de asignaciones de hoteles.
 *
 * Props:
 * - show: Booleano para mostrar/ocultar el modal.
 * - onHide: Callback para ocultar el modal.
 * - onApplyFilters: Callback que se invoca con el objeto de filtros cuando se aplica el filtro.
 */
const HotelFilterModal = ({ show, onHide, onApplyFilters }) => {
  // Estado local para cada filtro
  const [city, setCity] = useState('');
  const [hotel, setHotel] = useState('');
  const [date, setDate] = useState('');
  const [checkIn, setCheckIn] = useState('');
  const [checkOut, setCheckOut] = useState('');
  const [pax, setPax] = useState('');
  const [currency, setCurrency] = useState('');
  const [roomingList, setRoomingList] = useState('');
  const [proForma, setProForma] = useState('');
  const [factura, setFactura] = useState('');
  const [iga, setIga] = useState('');
  const [paymentDate, setPaymentDate] = useState('');
  const [paymentDoneDate, setPaymentDoneDate] = useState('');
  const [payedBy, setPayedBy] = useState('');

  // FunciÃ³n para limpiar todos los filtros
  const handleClear = () => {
    setCity('');
    setHotel('');
    setDate('');
    setCheckIn('');
    setCheckOut('');
    setPax('');
    setCurrency('');
    setRoomingList('');
    setProForma('');
    setFactura('');
    setIga('');
    setPaymentDate('');
    setPaymentDoneDate('');
    setPayedBy('');
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Construimos el objeto de filtros
    const filters = {
      city: city || undefined,
      hotel: hotel || undefined,
      date: date || undefined,
      check_in: checkIn || undefined,
      check_out: checkOut || undefined,
      pax: pax || undefined,
      currency: currency || undefined,
      rooming_list: roomingList || undefined,
      pro_forma: proForma || undefined,
      factura: factura || undefined,
      iga: iga || undefined,
      payment_date: paymentDate || undefined,
      payment_done_date: paymentDoneDate || undefined,
      payed_by: payedBy || undefined,
    };
    // Se eliminan las propiedades undefined
    const cleanedFilters = Object.fromEntries(
      Object.entries(filters).filter(([key, value]) => value !== undefined && value !== '')
    );
    onApplyFilters(cleanedFilters);
    onHide();
  };

  return (
    <Modal show={show} onHide={onHide} centered size="lg">
      <Form onSubmit={handleSubmit}>
        <Modal.Header closeButton>
          <Modal.Title>Filtrar Asignaciones de Hoteles</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Row>
            <Col md={4}>
              <Form.Group controlId="filterCity" className="mb-3">
                <Form.Label>Ciudad</Form.Label>
                <Form.Control
                  type="text"
                  placeholder="Ciudad"
                  value={city}
                  onChange={(e) => setCity(e.target.value)}
                />
              </Form.Group>
            </Col>
            <Col md={4}>
              <Form.Group controlId="filterHotel" className="mb-3">
                <Form.Label>Hotel</Form.Label>
                <Form.Control
                  type="text"
                  placeholder="Nombre del hotel"
                  value={hotel}
                  onChange={(e) => setHotel(e.target.value)}
                />
              </Form.Group>
            </Col>
            <Col md={4}>
              <Form.Group controlId="filterDate" className="mb-3">
                <Form.Label>Fecha</Form.Label>
                <Form.Control
                  type="date"
                  value={date}
                  onChange={(e) => setDate(e.target.value)}
                />
              </Form.Group>
            </Col>
          </Row>
          <Row>
            <Col md={4}>
              <Form.Group controlId="filterCheckIn" className="mb-3">
                <Form.Label>Check-in</Form.Label>
                <Form.Control
                  type="date"
                  value={checkIn}
                  onChange={(e) => setCheckIn(e.target.value)}
                />
              </Form.Group>
            </Col>
            <Col md={4}>
              <Form.Group controlId="filterCheckOut" className="mb-3">
                <Form.Label>Check-out</Form.Label>
                <Form.Control
                  type="date"
                  value={checkOut}
                  onChange={(e) => setCheckOut(e.target.value)}
                />
              </Form.Group>
            </Col>
            <Col md={4}>
              <Form.Group controlId="filterPax" className="mb-3">
                <Form.Label>PAX</Form.Label>
                <Form.Control
                  type="number"
                  placeholder="Cantidad de PAX"
                  value={pax}
                  onChange={(e) => setPax(e.target.value)}
                />
              </Form.Group>
            </Col>
          </Row>
          <Row>
            <Col md={4}>
              <Form.Group controlId="filterCurrency" className="mb-3">
                <Form.Label>Divisa</Form.Label>
                <Form.Control
                  type="text"
                  placeholder="Divisa (ej. EUR)"
                  value={currency}
                  onChange={(e) => setCurrency(e.target.value)}
                />
              </Form.Group>
            </Col>
            <Col md={4}>
              <Form.Group controlId="filterRoomingList" className="mb-3">
                <Form.Label>Rooming List</Form.Label>
                <Form.Select
                  value={roomingList}
                  onChange={(e) => setRoomingList(e.target.value)}
                >
                  <option value="">Todos</option>
                  <option value="true">SÃ­</option>
                  <option value="false">No</option>
                </Form.Select>
              </Form.Group>
            </Col>
            <Col md={4}>
              <Form.Group controlId="filterProForma" className="mb-3">
                <Form.Label>Pro Forma</Form.Label>
                <Form.Select
                  value={proForma}
                  onChange={(e) => setProForma(e.target.value)}
                >
                  <option value="">Todos</option>
                  <option value="true">SÃ­</option>
                  <option value="false">No</option>
                </Form.Select>
              </Form.Group>
            </Col>
          </Row>
          <Row>
            <Col md={4}>
              <Form.Group controlId="filterFactura" className="mb-3">
                <Form.Label>Factura Emitida</Form.Label>
                <Form.Select
                  value={factura}
                  onChange={(e) => setFactura(e.target.value)}
                >
                  <option value="">Todos</option>
                  <option value="true">SÃ­</option>
                  <option value="false">No</option>
                </Form.Select>
              </Form.Group>
            </Col>
            <Col md={4}>
              <Form.Group controlId="filterIga" className="mb-3">
                <Form.Label>IGA Gestionado</Form.Label>
                <Form.Select
                  value={iga}
                  onChange={(e) => setIga(e.target.value)}
                >
                  <option value="">Todos</option>
                  <option value="true">SÃ­</option>
                  <option value="false">No</option>
                </Form.Select>
              </Form.Group>
            </Col>
            <Col md={4}>
              <Form.Group controlId="filterPayedBy" className="mb-3">
                <Form.Label>Pagado por</Form.Label>
                <Form.Control
                  type="text"
                  placeholder="Nombre del pagador"
                  value={payedBy}
                  onChange={(e) => setPayedBy(e.target.value)}
                />
              </Form.Group>
            </Col>
          </Row>
          <Row>
            <Col md={6}>
              <Form.Group controlId="filterPaymentDate" className="mb-3">
                <Form.Label>Fecha de Pago</Form.Label>
                <Form.Control
                  type="date"
                  value={paymentDate}
                  onChange={(e) => setPaymentDate(e.target.value)}
                />
              </Form.Group>
            </Col>
            <Col md={6}>
              <Form.Group controlId="filterPaymentDoneDate" className="mb-3">
                <Form.Label>Fecha de Pago Realizado</Form.Label>
                <Form.Control
                  type="date"
                  value={paymentDoneDate}
                  onChange={(e) => setPaymentDoneDate(e.target.value)}
                />
              </Form.Group>
            </Col>
          </Row>
        </Modal.Body>
        <Modal.Footer className="d-flex justify-content-between">
          <Button variant="secondary" onClick={() => { handleClear(); onHide(); }}>
            Limpiar Filtros
          </Button>
          <Button variant="primary" type="submit">
            Aplicar Filtros
          </Button>
        </Modal.Footer>
      </Form>
    </Modal>
  );
};

HotelFilterModal.propTypes = {
  show: PropTypes.bool.isRequired,
  onHide: PropTypes.func.isRequired,
  onApplyFilters: PropTypes.func.isRequired,
};

export default HotelFilterModal;
