import React, { useState, useEffect } from 'react';
import PropTypes from 'prop-types';
import { Modal, Button, Form, Row, Col, Alert } from 'react-bootstrap';


// Función para calcular la edad a partir de la fecha de nacimiento
const calculateAge = (birthDate) => {
  if (!birthDate) return '';
  const birth = new Date(birthDate);
  const diff = Date.now() - birth.getTime();
  const ageDate = new Date(diff);
  return Math.abs(ageDate.getUTCFullYear() - 1970);
};


const RoomAssignmentModal = ({ show, onHide, initialData, onSave, availableHotels }) => {
  // Campos editables:
  const [hotelId, setHotelId] = useState(initialData.id_hotel || '');
  const [roomType, setRoomType] = useState(initialData.type || '');
  const [roomNumber, setRoomNumber] = useState(initialData.room_number || '');
  const [notes, setNotes] = useState(initialData.comments || '');
  const [validationMsg, setValidationMsg] = useState('');

  // Opciones para tipo y número de habitación (simuladas por ahora)
  const [roomTypeOptions, setRoomTypeOptions] = useState([]);
  const [roomNumberOptions, setRoomNumberOptions] = useState([]);

  // Nuevos estados para datos que se actualizan automáticamente al seleccionar hotel:
  const [city, setCity] = useState(initialData.city || '');
  const [checkIn, setCheckIn] = useState(initialData.start_date ? initialData.start_date.split('T')[0] : '');
  const [checkOut, setCheckOut] = useState(initialData.end_date ? initialData.end_date.split('T')[0] : '');

  // Cuando se selecciona un hotel, se actualizan las opciones de habitación y se actualizan ciudad, check-in y check-out.
  useEffect(() => {
    if (hotelId) {
      // Simulación de tipos de habitación:
      const simulatedTypes = hotelId === '5' ? ['Doble', 'Individual'] : ['Doble'];
      setRoomTypeOptions(simulatedTypes);
      // Simulación de números de habitación:
      const simulatedRooms = hotelId === '5' ? ['101', '102', '103'] : ['201', '202'];
      setRoomNumberOptions(simulatedRooms);
      
      // Actualizar ciudad, check-in y check-out usando los datos del hotel seleccionado.
      const selectedHotel = availableHotels.find(h => h.id_hotel.toString() === hotelId);
      if (selectedHotel) {
        setCity(selectedHotel.city || '');
        setCheckIn(selectedHotel.start_date ? selectedHotel.start_date.split('T')[0] : '');
        setCheckOut(selectedHotel.end_date ? selectedHotel.end_date.split('T')[0] : '');
      }
    }
  }, [hotelId, availableHotels]);

  // Función para guardar la asignación
  const handleSubmit = (e) => {
    e.preventDefault();
    if (!hotelId) {
      setValidationMsg('Debe seleccionar un hotel.');
      return;
    }
    if (!roomType) {
      setValidationMsg('Debe seleccionar un tipo de habitación.');
      return;
    }
    if (!roomNumber) {
      setValidationMsg('Debe seleccionar o ingresar el número de habitación.');
      return;
    }
    const payload = {
      id: initialData.id, // en edición, la asignación ya tiene id
      id_group: initialData.id_group,
      client_id: initialData.id_clients,
      date: initialData.date, // no se modifica
      city: city,
      hotel_id: parseInt(hotelId, 10),
      room_type: roomType,
      room_number: roomNumber,
      comments: notes,
    };
    onSave(payload)
      .then(() => {
        onHide();
      })
      .catch((err) => {
        setValidationMsg(err.message || 'Error al guardar la asignación.');
      });
  };

  return (
    <Modal show={show} onHide={onHide} centered size="lg">
      <Form onSubmit={handleSubmit}>
        <Modal.Header closeButton>
          <Modal.Title>{initialData.id ? 'Editar Asignación de Cuarto' : 'Asignar Cuarto'}</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          {/* Información del cliente (solo lectura) */}
          <Row className="mb-3">
            <Col>
              <strong>Cliente:</strong> {`${initialData.first_name} ${initialData.second_name} ${initialData.paternal_surname} ${initialData.mother_surname}`}
            </Col>
            <Col>
              <strong>Edad:</strong> {calculateAge(initialData.birth_date)}
            </Col>
          </Row>
          <Row className="mb-3">
            <Col>
              <strong>Sexo:</strong> {initialData.sex || '-'}
            </Col>
            <Col>
              <strong>Pasaporte:</strong> {initialData.passport || '-'}
            </Col>
          </Row>
          <Row className="mb-3">
            <Col>
              <strong>Fecha:</strong> {initialData.date}
            </Col>
          </Row>
          {/* Campo para seleccionar hotel */}
          <Form.Group controlId="editHotel" className="mb-3">
            <Form.Label>Hotel</Form.Label>
            <Form.Select
              value={hotelId}
              onChange={(e) => setHotelId(e.target.value)}
            >
              <option value="">Seleccione un hotel</option>
              {availableHotels.map((h) => (
                <option key={h.id_hotel} value={h.id_hotel}>
                  {h.hotel_name}
                </option>
              ))}
            </Form.Select>
          </Form.Group>
          {/* Mostrar Ciudad y Fechas actualizadas (solo lectura) */}
          <Row className="mb-3">
            <Col md={4}>
              <Form.Group controlId="displayCity">
                <Form.Label>Ciudad</Form.Label>
                <Form.Control type="text" value={city} readOnly />
              </Form.Group>
            </Col>
            <Col md={4}>
              <Form.Group controlId="displayCheckIn">
                <Form.Label>Check-in</Form.Label>
                <Form.Control type="date" value={checkIn} readOnly />
              </Form.Group>
            </Col>
            <Col md={4}>
              <Form.Group controlId="displayCheckOut">
                <Form.Label>Check-out</Form.Label>
                <Form.Control type="date" value={checkOut} readOnly />
              </Form.Group>
            </Col>
          </Row>
          {/* Campos editables: Tipo de habitación, Número de habitación y Notas */}
          <Form.Group controlId="editRoomType" className="mb-3">
            <Form.Label>Tipo de Habitación</Form.Label>
            <Form.Select
              value={roomType}
              onChange={(e) => setRoomType(e.target.value)}
            >
              <option value="">Seleccione un tipo</option>
              {roomTypeOptions.map((type) => (
                <option key={type} value={type}>
                  {type}
                </option>
              ))}
            </Form.Select>
          </Form.Group>
          <Form.Group controlId="editRoomNumber" className="mb-3">
            <Form.Label>Número de Habitación</Form.Label>
            <Form.Control
              type="text"
              value={roomNumber}
              onChange={(e) => setRoomNumber(e.target.value)}
              placeholder="Ingrese el número de habitación"
            />
          </Form.Group>
          <Form.Group controlId="editNotes" className="mb-3">
            <Form.Label>Notas / Comentarios</Form.Label>
            <Form.Control
              as="textarea"
              rows={3}
              value={notes}
              onChange={(e) => setNotes(e.target.value)}
            />
          </Form.Group>
          {validationMsg && <Alert variant="danger">{validationMsg}</Alert>}
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={onHide}>Cancelar</Button>
          <Button variant="primary" type="submit">Guardar Cambios</Button>
        </Modal.Footer>
      </Form>
    </Modal>
  );
};

RoomAssignmentModal.propTypes = {
  show: PropTypes.bool.isRequired,
  onHide: PropTypes.func.isRequired,
  initialData: PropTypes.object.isRequired,
  onSave: PropTypes.func.isRequired,
  availableHotels: PropTypes.array.isRequired,
};

export default RoomAssignmentModal;
