import React from "react";
import { Outlet } from "react-router-dom";
import Header from "./Header";
import Sidebar from "./Sidebar";

// Este componente define el layout general para las páginas internas
const Layout = () => {
  return (
    <div>
      {/* Encabezado superior */}
      <Header />

      <div className="d-flex" style={{ marginTop: "70px" }}>
        {/* Menú lateral */}
        <Sidebar />

        {/* Contenido principal */}
        <main className="flex-grow-1 p-3">
          {/* Outlet es el contenedor de las páginas hijas según la ruta */}
          <Outlet />
        </main>
      </div>
    </div>
  );
};

export default Layout;
