import React, { useState } from 'react';
import { Container, Nav, Tab, Table } from 'react-bootstrap';

// Componente para el submenú de ciudades
const CitySubMenu = ({ cities, selectedCity, onSelectCity }) => {
  return (
    <Nav variant="tabs" activeKey={selectedCity} onSelect={onSelectCity} className="mb-3">
      {cities.map(city => (
        <Nav.Item key={city}>
          <Nav.Link eventKey={city}>{city}</Nav.Link>
        </Nav.Item>
      ))}
    </Nav>
  );
};

// Componente principal para la sección de cuartos
const RoomsPage = () => {
  // Datos falsos para el ejemplo
  const cities = ['CAMBRIDGE', 'DURHAM', 'EDIMBURGO', 'GLASGOW', 'DUBLIN'];
  const [selectedCity, setSelectedCity] = useState(cities[0]);

  // Datos falsos de asignaciones de cuartos
  const allRoomAssignments = [
    { id: 1, client: 'Juan Pérez', city: 'CAMBRIDGE', hotel: 'Hotel A', room: '101' },
    { id: 2, client: 'Ana Gómez', city: 'DUBLIN', hotel: 'Hotel B', room: '202' },
    { id: 3, client: 'Luis Rodríguez', city: 'CAMBRIDGE', hotel: 'Hotel A', room: '102' },
    { id: 4, client: 'Carla López', city: 'EDIMBURGO', hotel: 'Hotel C', room: '303' },
    // Más asignaciones...
  ];

  // Filtramos las asignaciones según la ciudad seleccionada
  const filteredRooms = allRoomAssignments.filter(room => room.city === selectedCity);

  return (
    <Container>
      <h2>Asignación de Cuartos</h2>
      {/* Submenú de ciudades */}
      <CitySubMenu
        cities={cities}
        selectedCity={selectedCity}
        onSelectCity={(city) => setSelectedCity(city)}
      />
      {/* Tabla de cuartos */}
      <Table responsive bordered>
        <thead>
          <tr>
            <th>Cliente</th>
            <th>Hotel</th>
            <th>Habitación</th>
          </tr>
        </thead>
        <tbody>
          {filteredRooms.map(room => (
            <tr key={room.id}>
              <td>{room.client}</td>
              <td>{room.hotel}</td>
              <td>{room.room}</td>
            </tr>
          ))}
          {filteredRooms.length === 0 && (
            <tr>
              <td colSpan="3" className="text-center">No hay asignaciones para {selectedCity}</td>
            </tr>
          )}
        </tbody>
      </Table>
    </Container>
  );
};

export default RoomsPage;
