import React from 'react';
import { Table, Button } from 'react-bootstrap';

/**
 * HotelAssignmentsTable muestra en forma tabular las asignaciones de hoteles.
 * 
 * Props:
 * - data: Array con las asignaciones de hoteles.
 * - onEditAssignment: Función callback que se invoca al presionar el botón "Editar"
 */
const HotelAssignmentsTable = ({ data, groupPax, onEditAssignment }) => {
  if (!data || data.length === 0) {
    return <p>No hay asignaciones de hoteles registradas.</p>;
  }

  // Función para determinar el color de la fila
  const getRowStyle = (assignment) => {
    // Supongamos que el backend devuelve un campo "assigned_pax" con la cantidad de pasajeros asignados para esa asignación.
    // Si no existe hotel asignado, o sea hotel_name está vacío, pintamos en rojo.
    console.log(assignment);
    console.log('assignment.hotel_name:', assignment.hotel_name);
    console.log('assignment.pax:', assignment.pax);
    if (!assignment.hotel_name) return { backgroundColor: '#ffcccc' }; // rojo claro
    // Si la cantidad asignada es menor que el total, pintamos en amarillo.
    if (assignment.pax < groupPax) return { backgroundColor: '#ffffcc' }; // amarillo claro
    // Sino, sin estilo especial
    return {};
  };

  return (
    <Table responsive hover>
      <thead>
        <tr>
          <th>Ciudad</th>
          <th>Fecha</th>
          <th>Hotel</th>
          <th>Check-in</th>
          <th>Check-out</th>
          <th>PAX</th>
          <th>Rooming List</th>
          <th>Pro Forma</th>
          <th>Moneda</th>
          <th>Total a Pagar</th>
          <th>Fecha de Pago</th>
          <th>Fecha de Pago Realizado</th>
          <th>Factura</th>
          <th>IGA</th>
          <th>Notas</th>
          <th>Acciones</th>
        </tr>
      </thead>
      <tbody>
        {data.map((assignment, index) => (
          <tr key={index} style={getRowStyle(assignment)}>
            <td>{assignment.city}</td>
            {/* Suponemos que assignment.date es una cadena ISO o similar */}
            <td>{assignment.date ? assignment.date.slice(0, 10) : ''}</td>
            <td>{assignment.hotel_name}</td>
            <td>{assignment.check_in}</td>
            <td>{assignment.check_out}</td>
            <td>{assignment.pax}</td>
            <td>{assignment.rooming_list ? 'Sí' : 'No'}</td>
            <td>{assignment.pro_forma ? 'Sí' : 'No'}</td>
            <td>{assignment.currency || ''}</td>
            <td>{assignment.total_to_pay != null ? assignment.total_to_pay : ''}</td>
            <td>{assignment.payment_date ? assignment.payment_date.slice(0, 10) : ''}</td>
            <td>{assignment.payment_done_date ? assignment.payment_done_date.slice(0, 10) : ''}</td>
            <td>{assignment.factura ? 'Sí' : 'No'}</td>
            <td>{assignment.iga ? 'Sí' : 'No'}</td>
            <td>{assignment.notes || ''}</td>
            <td>
              <Button variant="primary" size="sm" onClick={() => onEditAssignment(assignment)}>
                Editar
              </Button>
            </td>
          </tr>
        ))}
      </tbody>
    </Table>
  );
};

export default HotelAssignmentsTable;
