import React, { useState, useEffect } from 'react';
import { Modal, Button, Form } from 'react-bootstrap';

const DeleteOptionalModal = ({ 
  show, 
  onHide, 
  clientId, 
  groupId, 
  dayId, 
  optionalData, 
  onOptionalDeleted 
}) => {
  const [confirmText, setConfirmText] = useState('');
  const expectedText = optionalData ? `borrar ${optionalData.optional_name}` : '';

  const handleDelete = () => {
    fetch(`${process.env.REACT_APP_API_URL}/optionals_purchase?id_group=${encodeURIComponent(groupId)}&client_id=${encodeURIComponent(clientId)}&id_activity=${encodeURIComponent(optionalData.id_activity)}`, {
      method: 'DELETE'
    })
      .then(res => {
        if (res.ok) {
          onOptionalDeleted(optionalData);
          onHide();
        } else {
          return res.json().then(data => {
            alert('Error al eliminar el opcional: ' + (data.message || 'Error desconocido'));
          });
        }
      })
      .catch(error => {
        console.error('Error al eliminar opcional:', error);
        alert('Error al eliminar el opcional.');
      });
  };

  useEffect(() => {
    if (show) {
      setConfirmText('');
    }
  }, [show]);

  return (
    <Modal show={show} onHide={onHide} centered>
      <Modal.Header closeButton>
        <Modal.Title>Confirmar Borrado</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <p>
          Para borrar la actividad <strong>{optionalData?.optional_name}</strong>, escriba:
        </p>
        <p className="text-muted">
          borrar {optionalData?.optional_name}
        </p>
        <Form.Control
          type="text"
          value={confirmText}
          onChange={(e) => setConfirmText(e.target.value)}
          placeholder="Escribir aquÃ­..."
        />
      </Modal.Body>
      <Modal.Footer>
        <Button variant="secondary" onClick={onHide}>Cancelar</Button>
        <Button variant="danger" onClick={handleDelete} disabled={confirmText.trim() !== expectedText}>
          Confirmar
        </Button>
      </Modal.Footer>
    </Modal>
  );
};

export default DeleteOptionalModal;
